<?php/** * @copyright Copyright (C) 2016 Usha Singhai Neo Informatique Pvt. Ltd * @license https://www.gnu.org/licenses/gpl.html */namespace products\utils;use usni\UsniAdaptor;/** * DiscountUtil class file. *  * @package products\utils */class DiscountUtil{    /**     * Validate discounts     * @param array $productDiscounts     * @param Product $model     * @return boolean     */    public static function validateDiscounts($productDiscounts, $model)    {        $isValid = true;        foreach ($productDiscounts as $index => $productDiscount)        {            if ($productDiscount['price'] >= $model->price)            {                $isValid = false;                $model->addError('discounts', UsniAdaptor::t('products', 'Discounted price should be less then base price at row') . ' ' . ($index + 1));            }            if (strtotime($productDiscount['start_datetime']) >= strtotime($productDiscount['end_datetime']))            {                $isValid = false;                $model->addError('discounts', UsniAdaptor::t('products', 'Start date should be less then end date at row') . ' ' . ($index + 1));            }        }        return $isValid;    }}