<?php/** * @copyright Copyright (C) 2016 Usha Singhai Neo Informatique Pvt. Ltd * @license https://www.gnu.org/licenses/gpl.html */namespace products\utils;use usni\UsniAdaptor;/** * SpecialUtil class file. *  * @package products\utils */class SpecialUtil{    /**     * Validate discounts     * @param array $productSpecials     * @param Product $model     * @return boolean     */    public static function validateSpecials($productSpecials, $model)    {        $isValid = true;        foreach ($productSpecials as $index => $productSpecial)        {            if ($productSpecial['price'] >= $model->price)            {                $isValid = false;                $model->addError('specials', UsniAdaptor::t('products', 'Special price should be less then base price at row') . ' ' . ($index + 1));            }            if (strtotime($productSpecial['start_datetime']) >= strtotime($productSpecial['end_datetime']))            {                $isValid = false;                $model->addError('specials', UsniAdaptor::t('products', 'Start date should be less then end date at row') . ' ' . ($index + 1));            }        }        return $isValid;    }}